// Extract message data
const message = $input.item.json.message;
const chatId = message.chat.id;
const messageText = message.text || '';
const userId = message.from.id;
const userName = message.from.first_name || 'User';

// Define bot commands and patterns
const commands = {
  '/start': 'welcome',
  '/help': 'help',
  '/orders': 'list_orders',
  '/myorders': 'my_orders',
  '/stats': 'order_stats',
  '/pending': 'pending_orders',
  '/completed': 'completed_orders'
};

// Check for direct commands first
let commandType = 'ai_query'; // Default to AI processing
let orderNumber = null;

if (commands[messageText.toLowerCase().trim()]) {
  commandType = commands[messageText.toLowerCase().trim()];
} else {
  // Check for order number pattern (ORD-xxxxx or just numbers)
  const orderNumberMatch = messageText.match(/(?:ord[-_]?)?(\d{4,6})/i);
  if (orderNumberMatch) {
    commandType = 'order_details';
    orderNumber = orderNumberMatch[1];
  }
}

return {
  json: {
    chatId: chatId,
    userId: userId,
    userName: userName,
    messageText: messageText,
    originalMessage: messageText,
    commandType: commandType,
    orderNumber: orderNumber,
    timestamp: new Date().toISOString()
  }
};

// Prepare context for Gemini AI
const userQuery = $node["Parse Message"].json.messageText;
const userName = $node["Parse Message"].json.userName;
const businessStats = $input.item.json[0] || {};
const recentOrders = $node["Get Recent Orders"].json || [];

// Format business context
const businessContext = `
BUSINESS OVERVIEW:
- Total Orders: ${businessStats.total_orders || 0}
- Pending Orders: ${businessStats.pending_orders || 0}
- Completed Orders: ${businessStats.completed_orders || 0}
- Today's Orders: ${businessStats.today_orders || 0}
- Yesterday's Orders: ${businessStats.yesterday_orders || 0}
- Monthly Revenue: ‚Çπ${parseFloat(businessStats.monthly_revenue || 0).toFixed(2)}
- Weekly Revenue: ‚Çπ${parseFloat(businessStats.weekly_revenue || 0).toFixed(2)}
- Total Revenue: ‚Çπ${parseFloat(businessStats.total_revenue || 0).toFixed(2)}
- Average Order Value: ‚Çπ${parseFloat(businessStats.avg_order_value || 0).toFixed(2)}
- Last Order: ${businessStats.last_order_date ? new Date(businessStats.last_order_date).toLocaleDateString('en-IN') : 'N/A'}
`;

// Format recent orders context
let ordersContext = "\nRECENT ORDERS:\n";
recentOrders.slice(0, 10).forEach((order, index) => {
  ordersContext += `${index + 1}. Order #${order.order_number} - ${order.status.toUpperCase()} - ‚Çπ${parseFloat(order.total).toFixed(2)} - ${order.customer_name} - ${new Date(order.created_at).toLocaleDateString('en-IN')}\n`;
});

// Create comprehensive prompt for Gemini
const geminiPrompt = `You are an intelligent business assistant for an artisan marketplace. You help artisans manage their orders and business.

CONTEXT:
${businessContext}
${ordersContext}

USER: ${userName}
QUERY: "${userQuery}"

INSTRUCTIONS:
1. Answer naturally and conversationally
2. Use emojis appropriately for better UX
3. Format numbers clearly (‚Çπ for currency)
4. If asked about specific orders, reference the data above
5. For business advice, be helpful and actionable
6. If you need more specific data, suggest what commands to use
7. Keep responses under 300 words for Telegram
8. Use HTML formatting for better readability

For order-specific queries, you have access to recent order data. For customer communication help, provide professional templates.

Respond helpfully to: "${userQuery}"`;

return {
  json: {
    chatId: $node["Parse Message"].json.chatId,
    userName: userName,
    userQuery: userQuery,
    prompt: geminiPrompt,
    businessContext: businessStats,
    recentOrders: recentOrders.slice(0, 5) // Send fewer orders to Gemini
  }
};

// Format AI response for Telegram
const aiResponse = $input.item.json.message || $input.item.json.text || 'Sorry, I couldn\'t process that request.';
const userName = $node["Prepare AI Context"].json.userName;

// Clean up the response and ensure it's Telegram-friendly
let formattedResponse = aiResponse
  .replace(/\*\*(.*?)\*\*/g, '<b>$1</b>') // Convert **bold** to <b>bold</b>
  .replace(/\*(.*?)\*/g, '<i>$1</i>') // Convert *italic* to <i>italic</i>
  .replace(/`(.*?)`/g, '<code>$1</code>') // Convert `code` to <code>code</code>
  .replace(/\n\n/g, '\n') // Remove double line breaks
  .trim();

// Add a subtle AI signature
formattedResponse += '\n\nü§ñ <i>AI-powered response</i>';

return {
  json: {
    chatId: $node["Parse Message"].json.chatId,
    message: formattedResponse,
    parse_mode: 'HTML'
  }
};

// Enhanced order details formatting
const orderData = $input.item.json;

if (!orderData || orderData.length === 0) {
  return {
    json: {
      chatId: $node["Parse Message"].json.chatId,
      message: `‚ùå <b>Order #${$node["Parse Message"].json.orderNumber} not found</b>\n\nPlease check the order number and try again.\n\nüí° Try asking: "Show me my recent orders"`,
      parse_mode: 'HTML'
    }
  };
}

const order = orderData[0];
const orderDate = new Date(order.created_at).toLocaleDateString('en-IN', {
  day: 'numeric',
  month: 'short', 
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

const statusEmoji = {
  'pending': '‚è≥',
  'processing': 'üîÑ',
  'shipped': 'üöö',
  'delivered': '‚úÖ',
  'cancelled': '‚ùå',
  'completed': 'üéâ'
};

const shippingAddr = order.shipping_address ? JSON.parse(order.shipping_address) : {};

// Format products
let productsText = '';
if (order.products && order.quantities && order.prices) {
  for (let i = 0; i < order.products.length; i++) {
    productsText += `‚Ä¢ ${order.products[i]} (Qty: ${order.quantities[i]}) - ‚Çπ${parseFloat(order.prices[i]).toFixed(2)}\n`;
  }
}

const message = `üì¶ <b>Order Details</b>\n\n` +
  `üî¢ <b>Order:</b> #${order.order_number}\n` +
  `${statusEmoji[order.status] || 'üìã'} <b>Status:</b> ${order.status.toUpperCase()}\n` +
  `üí∞ <b>Total:</b> ‚Çπ${parseFloat(order.total).toFixed(2)}\n` +
  `üìÖ <b>Date:</b> ${orderDate}\n\n` +
  `üë§ <b>Customer Details:</b>\n` +
  `‚Ä¢ Name: ${order.customer_name}\n` +
  `‚Ä¢ Email: ${order.customer_email}\n` +
  `${order.customer_phone ? `‚Ä¢ Phone: ${order.customer_phone}\n` : ''}\n` +
  `üìç <b>Shipping Address:</b>\n` +
  `${shippingAddr.fullName || 'N/A'}\n` +
  `${shippingAddr.addressLine1 || 'N/A'}\n` +
  `${shippingAddr.city || 'N/A'}, ${shippingAddr.state || 'N/A'} ${shippingAddr.postalCode || 'N/A'}\n\n` +
  `üõçÔ∏è <b>Items:</b>\n${productsText}\n` +
  `üí¨ <b>Need help?</b> Ask me:\n` +
  `‚Ä¢ "Write message to customer about shipping"\n` +
  `‚Ä¢ "How to handle this order?"\n` +
  `‚Ä¢ "Customer service response for delay"`;

return {
  json: {
    chatId: $node["Parse Message"].json.chatId,
    message: message,
    parse_mode: 'HTML'
  }
};

// Enhanced my orders formatting with AI suggestions
const orders = $input.item.json;

if (!orders || orders.length === 0) {
  return {
    json: {
      chatId: $node["Parse Message"].json.chatId,
      message: `üì≠ <b>No orders found</b>\n\nYou don't have any orders yet. When customers place orders for your products, they'll appear here!\n\nüí° <b>Ask me:</b>\n‚Ä¢ "How to get more orders?"\n‚Ä¢ "Marketing tips for artisans"\n‚Ä¢ "Improve my product listings"`,
      parse_mode: 'HTML'
    }
  };
}

let message = `üìã <b>Your Recent Orders</b> (${orders.length})\n\n`;

orders.forEach((order, index) => {
  const orderDate = new Date(order.created_at).toLocaleDateString('en-IN', {
    day: 'numeric',
    month: 'short'
  });

  const statusEmoji = {
    'pending': '‚è≥',
    'processing': 'üîÑ', 
    'shipped': 'üöö',
    'delivered': '‚úÖ',
    'cancelled': '‚ùå',
    'completed': 'üéâ'
  };

  message += `${index + 1}. <b>#${order.order_number}</b>\n` +
    `   ${statusEmoji[order.status] || 'üìã'} ${order.status.toUpperCase()} | ‚Çπ${parseFloat(order.total).toFixed(2)}\n` +
    `   üë§ ${order.customer_name} | üìÖ ${orderDate}\n` +
    `   üõçÔ∏è ${order.item_count} item(s)\n\n`;
});

message += `ü§ñ <b>AI Assistant:</b>\n` +
  `‚Ä¢ Ask "${orders[0].order_number} details" for full info\n` +
  `‚Ä¢ "Help with pending orders" for management tips\n` +
  `‚Ä¢ "Write follow-up message for ${orders[0].customer_name}"\n\n` +
  `üìä Want insights? Ask "Analyze my order patterns"`;

return {
  json: {
    chatId: $node["Parse Message"].json.chatId,
    message: message,
    parse_mode: 'HTML'
  }
};

// Enhanced statistics with AI insights
const stats = $input.item.json[0];

if (!stats) {
  return {
    json: {
      chatId: $node["Parse Message"].json.chatId,
      message: `üìä <b>No statistics available</b>\n\nNo orders found for your account. Statistics will appear once you start receiving orders!\n\nü§ñ <b>Ask me for help:</b>\n‚Ä¢ "How to get my first order?"\n‚Ä¢ "Marketing strategies for artisans"\n‚Ä¢ "Optimize my product photos"`,
      parse_mode: 'HTML'
    }
  };
}

const completionRate = stats.total_orders > 0 ? Math.round((stats.completed_orders / stats.total_orders) * 100) : 0;
const avgOrderValue = stats.total_orders > 0 ? (parseFloat(stats.total_revenue) / stats.total_orders).toFixed(2) : '0.00';

let performance = '';
if (completionRate >= 90) performance = 'üî• Excellent';
else if (completionRate >= 75) performance = 'üëç Good';
else if (completionRate >= 50) performance = '‚ö†Ô∏è Needs Improvement';
else performance = 'üÜò Critical';

const message = `üìä <b>Your Business Statistics</b>\n\n` +
  `üìà <b>Order Overview:</b>\n` +
  `‚Ä¢ Total Orders: ${stats.total_orders}\n` +
  `‚Ä¢ Pending: ${stats.pending_orders} ‚è≥\n` +
  `‚Ä¢ Completed: ${stats.completed_orders} ‚úÖ\n` +
  `‚Ä¢ Today's Orders: ${stats.today_orders} üìÖ\n\n` +
  `üí∞ <b>Revenue:</b>\n` +
  `‚Ä¢ This Month: ‚Çπ${parseFloat(stats.monthly_revenue || 0).toFixed(2)}\n` +
  `‚Ä¢ All Time: ‚Çπ${parseFloat(stats.total_revenue || 0).toFixed(2)}\n` +
  `‚Ä¢ Average Order: ‚Çπ${avgOrderValue}\n\n` +
  `üìà <b>Performance:</b>\n` +
  `‚Ä¢ Completion Rate: ${completionRate}% ${performance}\n\n` +
  `ü§ñ <b>AI Insights:</b>\n` +
  `Ask me for detailed analysis:\n` +
  `‚Ä¢ "Analyze my business performance"\n` +
  `‚Ä¢ "How to improve completion rate?"\n` +
  `‚Ä¢ "Revenue growth strategies"\n` +
  `‚Ä¢ "Compare with last month"`;

return {
  json: {
    chatId: $node["Parse Message"].json.chatId,
    message: message,
    parse_mode: 'HTML'
  }
};
