// Extract message data
const message = $input.item.json.message;
const chatId = message.chat.id;
const messageText = message.text.toLowerCase().trim();
const userId = message.from.id;
const userName = message.from.first_name || 'User';

// Define bot commands and patterns
const commands = {
  '/start': 'welcome',
  '/help': 'help',
  '/orders': 'list_orders',
  '/myorders': 'my_orders',
  '/stats': 'order_stats',
  '/pending': 'pending_orders',
  '/completed': 'completed_orders'
};

// Check for order number pattern (ORD-xxxxx or just numbers)
const orderNumberMatch = messageText.match(/(?:ord[-_]?)?(\d{4,6})/i);

// Determine command type
let commandType = 'unknown';
let orderNumber = null;

if (commands[messageText]) {
  commandType = commands[messageText];
} else if (orderNumberMatch) {
  commandType = 'order_details';
  orderNumber = orderNumberMatch[1];
} else if (messageText.includes('order') && messageText.includes('status')) {
  commandType = 'order_status_query';
} else if (messageText.includes('customer') || messageText.includes('contact')) {
  commandType = 'customer_query';
} else if (messageText.includes('revenue') || messageText.includes('earning')) {
  commandType = 'revenue_query';
} else if (messageText.includes('product') || messageText.includes('item')) {
  commandType = 'product_query';
}

return {
  json: {
    chatId: chatId,
    userId: userId,
    userName: userName,
    messageText: messageText,
    originalMessage: message.text,
    commandType: commandType,
    orderNumber: orderNumber,
    timestamp: new Date().toISOString()
  }
};

// Format order details response
const orderData = $input.item.json;

if (!orderData || orderData.length === 0) {
  return {
    json: {
      chatId: $node["Parse Message"].json.chatId,
      message: `âŒ Order #${$node["Parse Message"].json.orderNumber} not found.\n\nPlease check the order number and try again.`,
      parse_mode: 'HTML'
    }
  };
}

const order = orderData[0];
const orderDate = new Date(order.created_at).toLocaleDateString('en-IN', {
  day: 'numeric',
  month: 'short', 
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

const statusEmoji = {
  'pending': 'â³',
  'processing': 'ğŸ”„',
  'shipped': 'ğŸšš',
  'delivered': 'âœ…',
  'cancelled': 'âŒ',
  'completed': 'ğŸ‰'
};

const shippingAddr = order.shipping_address ? JSON.parse(order.shipping_address) : {};

const message = `ğŸ“¦ <b>Order Details</b>\n\n` +
  `ğŸ”¢ <b>Order:</b> #${order.order_number}\n` +
  `${statusEmoji[order.status] || 'ğŸ“‹'} <b>Status:</b> ${order.status.toUpperCase()}\n` +
  `ğŸ’° <b>Total:</b> â‚¹${parseFloat(order.total).toFixed(2)}\n` +
  `ğŸ“… <b>Date:</b> ${orderDate}\n\n` +
  `ğŸ‘¤ <b>Customer Details:</b>\n` +
  `â€¢ Name: ${order.customer_name}\n` +
  `â€¢ Email: ${order.customer_email}\n\n` +
  `ğŸ“ <b>Shipping Address:</b>\n` +
  `${shippingAddr.fullName || 'N/A'}\n` +
  `${shippingAddr.addressLine1 || 'N/A'}\n` +
  `${shippingAddr.city || 'N/A'}, ${shippingAddr.state || 'N/A'} ${shippingAddr.postalCode || 'N/A'}\n\n` +
  `ğŸ›ï¸ <b>Items:</b>\n` +
  `â€¢ ${order.product_name} (Qty: ${order.quantity}) - â‚¹${parseFloat(order.item_price).toFixed(2)}\n\n` +
  `ğŸ“± Need to contact customer? Reply with:\n<code>"Contact customer for order ${order.order_number}"</code>`;

return {
  json: {
    chatId: $node["Parse Message"].json.chatId,
    message: message,
    parse_mode: 'HTML'
  }
};

// Format my orders response
const orders = $input.item.json;

if (!orders || orders.length === 0) {
  return {
    json: {
      chatId: $node["Parse Message"].json.chatId,
      message: `ğŸ“­ <b>No orders found</b>\n\nYou don't have any orders yet. When customers place orders for your products, they'll appear here!`,
      parse_mode: 'HTML'
    }
  };
}

let message = `ğŸ“‹ <b>Your Recent Orders</b> (${orders.length})\n\n`;

orders.forEach((order, index) => {
  const orderDate = new Date(order.created_at).toLocaleDateString('en-IN', {
    day: 'numeric',
    month: 'short'
  });

  const statusEmoji = {
    'pending': 'â³',
    'processing': 'ğŸ”„', 
    'shipped': 'ğŸšš',
    'delivered': 'âœ…',
    'cancelled': 'âŒ',
    'completed': 'ğŸ‰'
  };

  message += `${index + 1}. <b>#${order.order_number}</b>\n` +
    `   ${statusEmoji[order.status] || 'ğŸ“‹'} ${order.status.toUpperCase()} | â‚¹${parseFloat(order.total).toFixed(2)}\n` +
    `   ğŸ‘¤ ${order.customer_name} | ğŸ“… ${orderDate}\n` +
    `   ğŸ›ï¸ ${order.item_count} item(s)\n\n`;
});

message += `ğŸ’¡ <b>Quick Actions:</b>\n` +
  `â€¢ Send order number for details (e.g., "${orders[0].order_number}")\n` +
  `â€¢ /pending - View pending orders\n` +
  `â€¢ /stats - View your statistics`;

return {
  json: {
    chatId: $node["Parse Message"].json.chatId,
    message: message,
    parse_mode: 'HTML'
  }
};

// Format statistics response
const stats = $input.item.json[0];

if (!stats) {
  return {
    json: {
      chatId: $node["Parse Message"].json.chatId,
      message: `ğŸ“Š <b>No statistics available</b>\n\nNo orders found for your account. Statistics will appear once you start receiving orders!`,
      parse_mode: 'HTML'
    }
  };
}

const message = `ğŸ“Š <b>Your Business Statistics</b>\n\n` +
  `ğŸ“ˆ <b>Order Overview:</b>\n` +
  `â€¢ Total Orders: ${stats.total_orders}\n` +
  `â€¢ Pending: ${stats.pending_orders} â³\n` +
  `â€¢ Completed: ${stats.completed_orders} âœ…\n` +
  `â€¢ Today's Orders: ${stats.today_orders} ğŸ“…\n\n` +
  `ğŸ’° <b>Revenue:</b>\n` +
  `â€¢ This Month: â‚¹${parseFloat(stats.monthly_revenue || 0).toFixed(2)}\n` +
  `â€¢ All Time: â‚¹${parseFloat(stats.total_revenue || 0).toFixed(2)}\n\n` +
  `ğŸ“ˆ <b>Performance:</b>\n` +
  `â€¢ Completion Rate: ${stats.total_orders > 0 ? Math.round((stats.completed_orders / stats.total_orders) * 100) : 0}%\n` +
  `â€¢ Average Order Value: â‚¹${stats.total_orders > 0 ? (parseFloat(stats.total_revenue) / stats.total_orders).toFixed(2) : '0.00'}\n\n` +
  `ğŸš€ <b>Quick Actions:</b>\n` +
  `â€¢ /pending - View pending orders\n` +
  `â€¢ /myorders - Recent order list\n` +
  `â€¢ Ask "Revenue this week" for detailed insights`;

return {
  json: {
    chatId: $node["Parse Message"].json.chatId,
    message: message,
    parse_mode: 'HTML'
  }
};
